<script>
import { required, email } from 'vuelidate/lib/validators'
const TitleH3   = () => import('~/components/TitleH3.vue')
const TitleH4   = () => import('~/components/TitleH4.vue')
const ErrorBox  = () => import('~/components/ErrorBox.vue')
const ButtonBox = () => import('~/components/ButtonBox.vue')
export default {
  name: 'Newsletter',
  components: {
    TitleH3, TitleH4, ButtonBox
  },
  props: {
    title: {
      type: String,
      required: true
    },
    buttonText: {
      type: String,
      required: true
    },
    resultTitle: {
      type: String,
      required: true
    },
    resultText: {
      type: String,
      required: true
    }
  },
  data: () => ({
    timer: null,
    form: {
      email: '',
      firstname: '',
      interest: 'all'
    },
    error: {
      email: false,
      firstname: false
    },
    valid: {
      email: false,
      firstname: false,
    },
    result: {
      status: false
    },
    submiting: false,
    loading: false
  }),
  methods: {
    async send () {
      this.$v.$touch()
      if( this.$v.$invalid ) {
        this.error.email = true
        this.submiting = false
        if (this.timer) clearTimeout(this.timer) // limpa o timer de novo
        this.timer = setTimeout(() => this.error.email = false, 3000)
        return false
      }
      if ( !this.$v.$invalid ) {
        this.error.email = false
        this.valid.email = true
        this.error.firsname = false
        this.valid.firsname = true
        this.submiting = true
        setTimeout(() => this.loading = true, 1000)
        // await fetch(``, { mode: 'cors' })
        //   .then(response => response.json())
        //   .then(response => console.log(response))
        //   .catch(error => this.showResult(error))
      }
    },
    showResult (resource) {
      this.loading = false
      this.result.status = true
      this.result.text = resource.text
      this.error.email = false
      this.result.type = resource.type
      this.form.email = ''
      this.valid.email = false
      this.$v.$reset()
    }
  },
  validations: {
    form: {
      firstname: {
        required
      },
      email: {
        required,
        email
      },
      // interest: {
      //   required
      // }
    }
  }
}
</script>
